mapper.select:
select FUNC_ID,MOD_ID,FUNC_NAME,FUNC_DESC,FUNC_URL,FUNC_URL_JS,FUNC_URL_CSS,FUNC_ORDER,FUNC_ICON,LAST_CHG_USR,LAST_CHG_DT from ADMIN_SM_BUSI_FUNC



			AND FUNC_ID like #{condition.funcId,jdbcType=VARCHAR }
			AND FUNC_ID = #{condition.funcId,jdbcType=VARCHAR}




			AND MOD_ID like #{condition.modId,jdbcType=VARCHAR }
			AND MOD_ID = #{condition.modId,jdbcType=VARCHAR}




			AND FUNC_NAME like #{condition.funcName,jdbcType=VARCHAR }
			AND FUNC_NAME = #{condition.funcName,jdbcType=VARCHAR}




			AND FUNC_DESC like #{condition.funcDesc,jdbcType=VARCHAR }
			AND FUNC_DESC = #{condition.funcDesc,jdbcType=VARCHAR}




			AND FUNC_URL like #{condition.funcUrl,jdbcType=VARCHAR }
			AND FUNC_URL = #{condition.funcUrl,jdbcType=VARCHAR}





				AND FUNC_URL_JS like #{condition.funcUrlJs,jdbcType=VARCHAR }

			AND FUNC_URL_JS = #{condition.funcUrlJs,jdbcType=VARCHAR}





				AND FUNC_URL_CSS like #{condition.funcUrlCss,jdbcType=VARCHAR }

			AND FUNC_URL_CSS = #{condition.funcUrlCss,jdbcType=VARCHAR}




			AND FUNC_ORDER like #{condition.funcOrder,jdbcType=DECIMAL }
			AND FUNC_ORDER = #{condition.funcOrder,jdbcType=DECIMAL}




			AND FUNC_ICON like #{condition.funcIcon,jdbcType=VARCHAR }
			AND FUNC_ICON = #{condition.funcIcon,jdbcType=VARCHAR}





				AND LAST_CHG_USR like #{condition.lastChgUsr,jdbcType=VARCHAR }

			AND LAST_CHG_USR = #{condition.lastChgUsr,jdbcType=VARCHAR}





				AND LAST_CHG_DT like #{condition.lastChgDt,jdbcType=VARCHAR }

			AND LAST_CHG_DT = #{condition.lastChgDt,jdbcType=VARCHAR}


	${dataAuth}

order by ${sort}

