mapper.select:
select

from ADMIN_SM_FUNC_MOD



			AND MOD_ID like #{condition.modId,jdbcType=VARCHAR }
			AND MOD_ID = #{condition.modId,jdbcType=VARCHAR}




			AND MOD_CDE like #{condition.modCde,jdbcType=VARCHAR }
			AND MOD_CDE = #{condition.modCde,jdbcType=VARCHAR}




			AND MOD_NAME like #{condition.modName,jdbcType=VARCHAR }
			AND MOD_NAME = #{condition.modName,jdbcType=VARCHAR}




			AND MOD_DESC like #{condition.modDesc,jdbcType=VARCHAR }
			AND MOD_DESC = #{condition.modDesc,jdbcType=VARCHAR}




			AND IS_OUTER like #{condition.isOuter,jdbcType=VARCHAR }
			AND IS_OUTER = #{condition.isOuter,jdbcType=VARCHAR}




			AND IS_APP like #{condition.isApp,jdbcType=VARCHAR }
			AND IS_APP = #{condition.isApp,jdbcType=VARCHAR}




			AND USER_NAME like #{condition.userName,jdbcType=VARCHAR }
			AND USER_NAME = #{condition.userName,jdbcType=VARCHAR}




			AND PASSWORD like #{condition.password,jdbcType=VARCHAR }
			AND PASSWORD = #{condition.password,jdbcType=VARCHAR}




			AND USER_KEY like #{condition.userKey,jdbcType=VARCHAR }
			AND USER_KEY = #{condition.userKey,jdbcType=VARCHAR}




			AND PWD_KEY like #{condition.pwdKey,jdbcType=VARCHAR }
			AND PWD_KEY = #{condition.pwdKey,jdbcType=VARCHAR}





				AND LAST_CHG_USR like #{condition.lastChgUsr,jdbcType=VARCHAR }

			AND LAST_CHG_USR = #{condition.lastChgUsr,jdbcType=VARCHAR}




			AND LAST_CHG_DT like #{condition.lastChgDt,jdbcType=VARCHAR}
			AND LAST_CHG_DT = #{condition.lastChgDt,jdbcType=VARCHAR}


	${dataAuth}

order by ${sort}

mapper.insert:
insert into ADMIN_SM_FUNC_MOD
MOD_ID, MOD_CDE, MOD_NAME, MOD_DESC, IS_OUTER, IS_APP, USER_NAME, PASSWORD, USER_KEY, PWD_KEY, LAST_CHG_USR, LAST_CHG_DT
#{modId,jdbcType=VARCHAR}, #{modCde,jdbcType=VARCHAR}, #{modName,jdbcType=VARCHAR}, #{modDesc,jdbcType=VARCHAR}, #{isOuter,jdbcType=VARCHAR}, #{isApp,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{userKey,jdbcType=VARCHAR}, #{pwdKey,jdbcType=VARCHAR}, #{lastChgUsr,jdbcType=VARCHAR}, #{lastChgDt,jdbcType=VARCHAR}

mapper.update:
update ADMIN_SM_FUNC_MOD
MOD_ID = #{modId,jdbcType=VARCHAR}, MOD_CDE = #{modCde,jdbcType=VARCHAR}, MOD_NAME = #{modName,jdbcType=VARCHAR}, MOD_DESC = #{modDesc,jdbcType=VARCHAR}, IS_OUTER = #{isOuter,jdbcType=VARCHAR}, IS_APP = #{isApp,jdbcType=VARCHAR}, USER_NAME = #{userName,jdbcType=VARCHAR}, PASSWORD = #{password,jdbcType=VARCHAR}, USER_KEY = #{userKey,jdbcType=VARCHAR}, PWD_KEY = #{pwdKey,jdbcType=VARCHAR}, LAST_CHG_USR = #{lastChgUsr,jdbcType=VARCHAR}, LAST_CHG_DT = #{lastChgDt,jdbcType=VARCHAR}
where MOD_ID = #{modId,jdbcType=VARCHAR}
